import React from 'react';

import { Router, Switch, Route } from "react-router-dom";

import "./pages/aboutUs"
import './App.css';

function App() {
  const [data, setData] = React.useState(null);

  React. useEffect(() => {
    fetch("/api")
      .then((res) => res.json())
      .then((data) => setData(data.message));


//state variables
