  return (
    <div className="App">
      <h1>The Peeps</h1>
      <nav className="topnav">
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/feed">Feed</Link>
          </li>
        </ul>
      </nav>
      <fieldset>

            <form onSubmit={submitHandler}>
              <div>
              <input
              type = 'text'
              placeholder='Title'
              value = {title}
              onChange={(e) => setTitle(e.target.value)}
              />
              </div>
              <div>
              <input
              type = 'number'
              placeholder='Price'
              value = {price}
              onChange={(e) => setPrice(e.target.value)}
              />
              </div>
              <div>
              <input
              type = 'text'
              placeholder='Type'
              value = {type}
              onChange={(e) => setType(e.target.value)}
              />
              <div>
              <button type='submit' id='submit'>Add Item</button>
              </div>
              </div>
            </form>
        </fieldset>
      <Switch>
        <Route path="/admin">
          <Admin />
        </Route>
        <Route path="/feed">
          <Feed />
        </Route>
        <Route path="/">
          <Home />
        </Route>
      </Switch>
    </div>
  );

}

export default App;
